mapper.select:
SELECT DATEADD(day, DATEDIFF(day, 0, a.fldSignDate), 0) as reportDate ,b.fldUserName as userName ,d.fldDeptName as deptName ,sum(a.fldPerformanceMoney) as totalMoney FROM tblCustomerContract a LEFT JOIN tblUser b ON a.fldFinancialUserNo = b.fldLoginName LEFT JOIN tblUserDeptLink c ON b.fldLoginName = c.fldLoginName LEFT JOIN tblDept d ON c.fldDeptCode = d.fldDeptCode where 1=1
and d.fldDeptName like #{deptName}
and b.fldUserName like #{userName}
and DATEADD(day, DATEDIFF(day, 0, a.fldSignDate), 0) >= #{startDate}
and DATEADD(day, DATEDIFF(day, 0, a.fldSignDate), 0) <= #{endDate}
GROUP BY DATEADD(day, DATEDIFF(day, 0, a.fldSignDate), 0) ,b.fldUserName ,d.fldDeptName

SELECT DATEADD(month, DATEDIFF(month, 0, a.fldSignDate), 0) as reportDate ,b.fldUserName as userName ,d.fldDeptName as deptName ,sum(a.fldPerformanceMoney) as totalMoney FROM tblCustomerContract a LEFT JOIN tblUser b ON a.fldFinancialUserNo = b.fldLoginName LEFT JOIN tblUserDeptLink c ON b.fldLoginName = c.fldLoginName LEFT JOIN tblDept d ON c.fldDeptCode = d.fldDeptCode where 1=1
and d.fldDeptName like #{deptName}
and b.fldUserName like #{userName}
and DATEADD(month, DATEDIFF(month, 0, a.fldSignDate), 0) >= #{startDate}
and DATEADD(month, DATEDIFF(month, 0, a.fldSignDate), 0) <= #{endDate}
GROUP BY DATEADD(month, DATEDIFF(month, 0, a.fldSignDate), 0) ,b.fldUserName ,d.fldDeptName

select a.* ,isnull(b.totalValidCallNum,0) as totalValidCallNum ,c.item0 ,c.item3 ,c.item5 ,c.item9 ,c.item10 ,d.count0 ,d.count3 ,d.count5 ,d.count9 ,d.count10 from (SELECT DATEADD(day, DATEDIFF(day, 0, a.fldCallDate), 0) AS reportDate ,b.fldUserName AS userName ,d.fldDeptName AS deptName ,isnull(sum(isnull(a.fldCallLong, 0)),0) AS totalValidCallLong ,isnull(sum(isnull(a.fldTotalDuration, 0)),0) AS totalCallLong ,count(DISTINCT a.fldPhone) AS totalCutomerNum ,count(*) AS totalCallNum FROM tblTelephoneRecord a LEFT JOIN tblUser b ON a.fldCreateUserNo = b.fldLoginName LEFT JOIN tblUserDeptLink c ON b.fldLoginName = c.fldLoginName LEFT JOIN tblDept d ON c.fldDeptCode = d.fldDeptCode WHERE 1=1
and d.fldDeptName like #{deptName}
and b.fldUserName like #{userName}
and a.fldCallDate >= #{startDate}
and a.fldCallDate <= #{endDate}
AND a.fldCallType = 0 GROUP BY DATEADD(day, DATEDIFF(day, 0, a.fldCallDate), 0) ,b.fldUserName ,d.fldDeptName) a left join (SELECT DATEADD(day, DATEDIFF(day, 0, a.fldCallDate), 0) AS reportDate ,b.fldUserName AS userName ,d.fldDeptName AS deptName ,count(*) AS totalValidCallNum FROM tblTelephoneRecord a LEFT JOIN tblUser b ON a.fldCreateUserNo = b.fldLoginName LEFT JOIN tblUserDeptLink c ON b.fldLoginName = c.fldLoginName LEFT JOIN tblDept d ON c.fldDeptCode = d.fldDeptCode WHERE 1=1 AND a.fldCalllong != 0
and d.fldDeptName like #{deptName}
and b.fldUserName like #{userName}
and a.fldCallDate>= #{startDate}
and a.fldCallDate <= #{endDate}
AND a.fldCallType = 0 GROUP BY DATEADD(day, DATEDIFF(day, 0, a.fldCallDate), 0) ,b.fldUserName ,d.fldDeptName) b on a.reportDate = b.reportDate and a.userName = b.userName and a.deptName = b.deptName left join (SELECT b.reportDate ,b.userName ,b.deptName ,isnull(b.[0], 0) AS item0 ,isnull(b.[3], 0) AS item3 ,isnull(b.[5], 0) AS item5 ,isnull(b.[9], 0) AS item9 ,isnull(b.[10], 0) AS item10 FROM ( SELECT * FROM ( SELECT DATEADD(day, DATEDIFF(day, 0, a.fldCallDate), 0) AS reportDate ,b.fldUserName AS userName ,d.fldDeptName AS deptName ,a.fldResultType ,sum(fldCallLong) calllong FROM tblTelephoneRecord a LEFT JOIN tblUser b ON a.fldCreateUserNo = b.fldLoginName LEFT JOIN tblUserDeptLink c ON b.fldLoginName = c.fldLoginName LEFT JOIN tblDept d ON c.fldDeptCode = d.fldDeptCode WHERE 1 = 1 AND a.fldCalllong != 0
and d.fldDeptName like #{deptName}
and b.fldUserName like #{userName}
and a.fldCallDate>= #{startDate}
and a.fldCallDate <= #{endDate}
AND a.fldCallType = 0 GROUP BY DATEADD(day, DATEDIFF(day, 0, a.fldCallDate), 0) ,b.fldUserName ,d.fldDeptName ,a.fldResultType ) a pivot(sum(a.calllong) FOR a.fldResultType IN ([0], [3], [5], [9], [10])) AS ourpivot ) b ) c on a.reportDate = c.reportDate and a.userName = c.userName and a.deptName = c.deptName left join (SELECT b.reportDate ,b.userName ,b.deptName ,isnull(b.[0], 0) AS count0 ,isnull(b.[3], 0) AS count3 ,isnull(b.[5], 0) AS count5 ,isnull(b.[9], 0) AS count9 ,isnull(b.[10], 0) AS count10 FROM ( SELECT * FROM ( SELECT DATEADD(day, DATEDIFF(day, 0, a.fldCallDate), 0) AS reportDate ,b.fldUserName AS userName ,d.fldDeptName AS deptName ,a.fldResultType ,COUNT(*) AS num FROM tblTelephoneRecord a LEFT JOIN tblUser b ON a.fldCreateUserNo = b.fldLoginName LEFT JOIN tblUserDeptLink c ON b.fldLoginName = c.fldLoginName LEFT JOIN tblDept d ON c.fldDeptCode = d.fldDeptCode WHERE 1 = 1 AND a.fldCalllong != 0
and d.fldDeptName like #{deptName}
and b.fldUserName like #{userName}
and a.fldCallDate>= #{startDate}
and a.fldCallDate <= #{endDate}
AND a.fldCallType = 0 GROUP BY DATEADD(day, DATEDIFF(day, 0, a.fldCallDate), 0) ,b.fldUserName ,d.fldDeptName ,a.fldResultType ) a pivot(sum(a.num) FOR a.fldResultType IN ([0], [3], [5], [9], [10])) AS ourpivot ) b ) d on a.reportDate = d.reportDate and a.userName = d.userName and a.deptName = d.deptName WHERE 1=1
and a.totalValidCallLong > #{totalCallLongStart}
and a.totalValidCallLong < #{totalCallLongEnd}
order by a.reportDate, a.totalValidCallLong desc

select a.*, isnull(b.totalValidCallNum,0) as totalValidCallNum from (SELECT DATEADD(day, DATEDIFF(day, 0, a.fldCallDate), 0) AS reportDate ,b.fldUserName AS userName ,d.fldDeptName AS deptName ,isnull(sum(isnull(a.fldCallLong, 0)),0) AS totalValidCallLong ,isnull(sum(isnull(a.fldTotalDuration, 0)),0) AS totalCallLong ,count(DISTINCT a.fldPhone) AS totalCutomerNum ,count(*) AS totalCallNum FROM tblTelephoneRecord a LEFT JOIN tblUser b ON a.fldCreateUserNo = b.fldLoginName LEFT JOIN tblUserDeptLink c ON b.fldLoginName = c.fldLoginName LEFT JOIN tblDept d ON c.fldDeptCode = d.fldDeptCode WHERE 1=1
and d.fldDeptName like #{deptName}
and b.fldUserName like #{userName}
and a.fldCallDate >= #{startDate}
and a.fldCallDate <= #{endDate}
AND a.fldCallType = 1 GROUP BY DATEADD(day, DATEDIFF(day, 0, a.fldCallDate), 0) ,b.fldUserName ,d.fldDeptName) a left join (SELECT DATEADD(day, DATEDIFF(day, 0, a.fldCallDate), 0) AS reportDate ,b.fldUserName AS userName ,d.fldDeptName AS deptName ,count(*) AS totalValidCallNum FROM tblTelephoneRecord a LEFT JOIN tblUser b ON a.fldCreateUserNo = b.fldLoginName LEFT JOIN tblUserDeptLink c ON b.fldLoginName = c.fldLoginName LEFT JOIN tblDept d ON c.fldDeptCode = d.fldDeptCode WHERE 1=1 AND a.fldCalllong != 0
and d.fldDeptName like #{deptName}
and b.fldUserName like #{userName}
and a.fldCallDate >= #{startDate}
and a.fldCallDate <= #{endDate}
AND a.fldCallType = 1 GROUP BY DATEADD(day, DATEDIFF(day, 0, a.fldCallDate), 0) ,b.fldUserName ,d.fldDeptName) b on a.reportDate = b.reportDate and a.userName = b.userName and a.deptName = b.deptName WHERE 1=1
and a.totalValidCallLong > #{totalCallLongStart}
and a.totalValidCallLong < #{totalCallLongEnd}
order by a.reportDate, a.totalValidCallLong desc
